def split_list(data):
    sublists = []
    sublist = []
    total_revenue_count = 0

    for item in data:
        if item == "Total Revenue":
            total_revenue_count += 1
            if total_revenue_count > 2:
                sublists.append(sublist)
                sublist = []
        sublist.append(item)

    # Append the last sublist
    sublists.append(sublist)

    return sublists

